<?php

namespace Concurso\Menus4AllBundle\Entity;

use Doctrine\ORM\EntityRepository;

/**
 * MenuRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class MenuRepository extends EntityRepository {

    public function getRecetasDescartadas($idsReceta,$idMenu) {
        $em = $this->getDoctrine()->getEntityManager();
        // Todas las recetas cuya id no se encuentra en el array recibido y pertenezca al menÃº actual
        $query = $em->createQuery("SELECT r FROM ConcursoMenus4AllBundle:Receta r where r.id in (:ids) AND m.id = :idMenu ")
                ->setParameter(array(
                    "ids" => $idsReceta,
                    "idMenu" => $idMenu
                )
        );

        return $query->getResult();
    }

}
